mapper.select:
select o.id, o.userName,o.userTel,o.peopleNum,o.departurePlace,o.destination,o.checkStartTime, o.checkEndTime,o.state,d.checkName,d.checkTel from tb_automobile_order o left join tb_automobile_order_detail d on o.id=d.t_id where 1=1 and d.state=0
and o.state=#{state}
order by o.createTime desc limit ${page.num},${page.size}

select * from tb_automobile_order_detail where 1=1 and state=0
and t_id=#{t_id}
order by createTime desc
