mapper.select:
select v.id, v.userName,v.userTel,v.peopleNum,v.address,v.checkStartTime,v.checkEndTime,v.state,d.checkName,d.checkTel from tb_visa_order v left join tb_visa_order_detail d on v.id=d.v_id where d.state=0
and v.state=#{state}
order by v.createTime desc limit ${page.num},${page.size}

select * from tb_visa_order_detail where 1=1 and state=0
and v_id=#{v_id}
order by createTime desc
